import React, { Component } from "react";
import { foodCategories, foodDetail } from "./categories";

const CategoriesContext = React.createContext();

class CategoriesProvider extends Component {
  state = {
    categories: foodCategories,
    cart: []
  };

  getItem = id => {
    const meal = this.state.products.find(item => item.id === id);
    return meal;
  };
  handleDetail = () => {
    console.log("Hello form detail");
  };

  addToCart = id => {
    let tempMeal = [...this.state.categories];
    const index = tempMeal.indexOf(this.getItem(id));
    const meal = tempMeal[index];
    meal.inCart = true;
    meal.count = 1;
    const price = meal.price;
    meal.total = price;
    this.setState(
      () => {
        return { categories: tempMeal, cart: [...this.state.cart] };
      },
      () => {
        console.log(this.state);
      }
    );
  };
  render() {
    return (
      <CategoriesContext.Provider
        value={{
          ...this.state,
          handleDetail: this.handleDetail,
          addToCart: this.addToCart
        }}
      >
        {this.props.children}
      </CategoriesContext.Provider>
    );
  }
}

const CategoriesConsumer = CategoriesContext.Consumer;

export { CategoriesProvider, CategoriesConsumer };
