import React, { Component } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { CategoriesConsumer } from "../context";
import PropTypes from "prop-types";
import Categories from "./Categories";

export default class Meals extends Component {
  render() {
    const { id, title, img, inCart, price } = this.props.categories;
    return (
      <CategoriesWrap className="col-9 mx-auto col-md-6 col-lg-3 my-3">
        <div className="card">
          <CategoriesConsumer>
            {value => {
              <div className="img-container p-5">
                <Link to="/details">
                  <img src={img} alt="Categories" className="card-img-top" />
                </Link>
                <button
                  className="card-btn"
                  disabled={inCart ? true : false}
                  onClick={() => {
                    value.addToCart(id);
                  }}
                >
                  {inCart ? (
                    <p className="text-capitalize mb-0" disabled>
                      {" "}
                      inCart{" "}
                    </p>
                  ) : (
                    <i className="fas fa-cart-plus" />
                  )}
                </button>
              </div>;
            }}
          </CategoriesConsumer>
          <div className="card-footer d-flex justify-content-between">
            <p className="align-self-center mb-0">{title}</p>
            <h5 className="text-red font-italic mb-0">
              <span className="mr-1">KSH{price}</span>
            </h5>
          </div>
        </div>
      </CategoriesWrap>
    );
  }
}

// Categories.propTypes = {
